package chapter07;import lombok.extern.slf4j.Slf4j;import java.util.concurrent.atomic.AtomicIntegerArray;/** *  * @author tengfei.fangtf * @version $Id: AtomicIntegerArrayTest.java, v 0.1 2015-8-1 上午12:04:44 tengfei.fangtf Exp $ */@Slf4jpublic class AtomicIntegerArrayTest {    static int[]              value = new int[] { 1, 2 };    static AtomicIntegerArray ai    = new AtomicIntegerArray(value);    public static void main(String[] args) {        ai.getAndSet(0, 3);        log.info("ai.get(0):{}", ai.get(0));        log.info("value[0]:{}", value[0]);    }}