package chapter08;import lombok.extern.slf4j.Slf4j;import java.util.concurrent.CyclicBarrier;@Slf4jpublic class CyclicBarrierTest {    static CyclicBarrier c = new CyclicBarrier(2);    public static void main(String[] args) {        new Thread(new Runnable() {            @Override            public void run() {                try {                    c.await();                } catch (Exception e) {                }                log.info("1");            }        }).start();        try {            c.await();        } catch (Exception e) {        }        log.info("2");    }}