package chapter08;import lombok.extern.slf4j.Slf4j;/** *  * @author tengfei.fangtf * @version $Id: JoinCountDownLatchTest.java, v 0.1 2015-8-1 上午12:08:30 tengfei.fangtf Exp $ */@Slf4jpublic class JoinCountDownLatchTest {    public static void main(String[] args) throws InterruptedException {        Thread parser1 = new Thread(new Runnable() {            @Override            public void run() {            }        });        Thread parser2 = new Thread(new Runnable() {            @Override            public void run() {                log.info("parser2 finish");            }        });        parser1.start();        parser2.start();        parser1.join();        parser2.join();        log.info("all parser finish");    }}